/* QUICK EXTEND FUNCTION */
function qExtend(ChildClass, ParentClass) {
	ChildClass.prototype = Object.create(ParentClass.prototype);
	ChildClass.prototype.constructor = ChildClass;
}

/**
 * Ajax call to get XML data from an FXG
 * @param {String} id   - The ID of the element
 * @param {boolean} ugc - If its a custom uploaded ugc file
 * @return {String} xml - XML data from FXG
 */
function getFXGXML(id, ugc) {
	var xmlData = null;
	$.ajax({
		type: 'GET',
		async: false,
		url: 'https://texel.envelopes.com/getFXG?type=' + ((ugc) ? 'ugc' : '') + '&id=' + id,
		timeout: 5000,
		dataType: 'xml'
	}).done(function(xml) {
		xmlData = xml;
	});

	return xmlData;
}

/**
 * Basic element type for Scene7
 * Will have the basic schema for all other elements
 * This should not be used directly, it is rather a base for actual Scene7 objects. IE richtext, bitmap, paths, etc
 * @param {String} id   - The ID of the element
 * @param {String} type - The element type
 * @constructor
 */
function Scene7Element(id, type) {
	this.id = id;
	this.type = type;

	//the below needs to be set via function calls
	this.parentX = 0;
	this.parentY = 0;
	this.x = 0;
	this.y = 0;
	this.width = 1;             //width of element in original FXG
	this.height = 1;            //height of element in original FXG
	this.scaleX = 1;
	this.scaleY = 1;
	this.rotation = 0;
	this.originalRotation = 0;
	this.deleted = false; //has element been deleted
	this.changed = false; //has element changed/edited
	this.custom = false;  //custom added element
	this.justAdded = false; //was element just added
	this.image = null; //holds the current image in base64 format
	this.editable = true; //whether the element can be changed
}

