/**
 * RichText element type for Scene7
 * Inherits Scene7Element
 * @param {String} id   - The ID of the element
 * @param {String} type - The element type
 * @constructor
 */
function Scene7RichText(id, type) {
	Scene7Element.call(this, id, type);

	this.text = '';
	this.tracking = 0;
	this.textAlign = 'left';
	this.lineHeight = 12;
	this.color = '#000000';
	this.fontFamily = 'Minion Pro';
	this.fontSize = 12;
	this.fontWeight = '';
	this.textDecoration = '';
	this.fitToSize = false; //this determines if we should fit the text inside the textbox so that it does not wrap lines
	this.isVariable = false;
	this.groupId = null;
}

/**
 * Bitmap element type for Scene7
 * Inherits Scene7Element
 * @param {String} id   - The ID of the element
 * @param {String} type - The element type
 * @constructor
 */
function Scene7Bitmap(id, type) {
	Scene7Element.call(this, id, type);

	this.leftCropAmt = 0;
	this.topCropAmt = 0;
	this.rightCropAmt = 0;
	this.bottomCropAmt = 0;

	this.lastCropWidth = 0;
	this.lastCropHeight = 0;
	this.cropStartX = 0;
	this.cropStartY = 0;
	this.cropWidth = 0;
	this.cropHeight = 0;
	this.zoom = 1;

	this.url = '';
}

/**
 * Path element type for Scene7
 * Inherits Scene7Element
 * @param {String} id   - The ID of the element
 * @param {String} type - The element type
 * @constructor
 */
function Scene7Path(id, type) {
	Scene7Element.call(this, id, type);

	this.color = '#FFFFFF';
	this.pathData = [];
	this.data = null;
}

qExtend(Scene7RichText, Scene7Element);
qExtend(Scene7Bitmap, Scene7Element);
qExtend(Scene7Path, Scene7Element);

function cmykToHex(c, m, y, k) {
	r = 255 - Math.round(2.55 * (c+k));
	g = 255 - Math.round(2.55 * (m+k));
	b = 255 - Math.round(2.55 * (y+k));
	if(r<0) r = 0 ;
	if(g<0) g = 0 ;
	if(b<0) b = 0 ;
	return rgbToHex(r, g, b);
}

function rgbToHex(r, g, b) {
	return "#" + (String("0" + r.toString(16)).slice(-2) + String("0" + g.toString(16)).slice(-2) + String("0" + b.toString(16)).slice(-2)).toUpperCase();
}

function makeFullHex(input) {
	if(typeof input !== 'undefined') {
		var length = input.length;
		var result = '#';
		if (length == 7) { return input; }
		if (length != 4 && length != 3) { return '#000000';	}
		for (var i = 1; i < input.length; i++) {
			if (length == 3) { result += input.charAt(i) + input.charAt(i) + input.charAt(i); }
			else { result += input.charAt(i) + input.charAt(i); }
		}
		return result;
	} else { return "#000000"; }
}

function charEncodingForUser(value) {
	return value.replace("’", "'").replace("é", "e");
}

function charEncodingForScene7(value) {
	value = typeof value !== 'undefined' ? value : '';

	return value.replace(/’/g, "'")
		.replace(/&/g, '&amp;')
		.replace(/</g, '&lt;')
		.replace(/>/g, '&gt;')
		.replace(/"/g, '&quot;')
		.replace(/'/g, '&apos;')
		.replace(/%/g, '&#37;')
		.replace(/\+/g, '&#43;');
}

function replaceBreakLines(text) {
	if(typeof text !== 'undefined') {
		return text.replace(/(?:\\r\\n)|(?:<br\s*\/*>)/g, '\n');
	} else {
		return '';
	}
}

function isCommandKey(code) {
	var ua = window.navigator.userAgent;
	if (((ua.indexOf('OPR/') >= 0 || ua.indexOf('Safari/') >= 0 || ua.indexOf('Chrome/') >= 0) && code == '91') || (ua.indexOf('Firefox') >= 0 && code == '224') || (ua.indexOf('MSIE ') >= 0 && code == '17')) {
		return true;
	}

	return false;
}

