	/**
	 * Make the texel max size of browser, responsive
	 */
	makeFullSize: function() {
		var self = this;

		function resizeTexel() {
			//reset the size
			self.options.staticWidth = 300;
			self.options.staticHeight = 300;

			while(self.element.width() > (self.options.staticWidth + 250) && (self.element.height() - $('.texelTemplateHelp').outerHeight() - 20) > (self._globalVars.templateHeight / self._globalVars.templateWidth * (self.options.staticWidth + 75))) {
				self.options.staticWidth = self.options.staticWidth + 50;
				self.options.staticHeight = self._globalVars.templateHeight / self._globalVars.templateWidth * self.options.staticWidth;
			}

			self._globalVars.designerEl.animate({
				'width' : self.options.staticWidth,
				'height' : self.options.staticHeight
			}, {
				duration: 0,
				complete: function() {
					self._paintTexel(self.options.activeDesign, self.options.initialLoad, null, true);

					if($('.texelPopupContainer').length > 0) {
						self._updateToolPosition($('#' + $('.texelPopupContainer').attr('data-targetedElementId')));
					}
					$('.texel_container .newTemplate').css('left', ($('.texel_designer').offset().left - $('.texel_container').offset().left - 96) + 'px'); //335 left, 30 bottom
					$('.texel_container .saveClose').css('left', ($('.texel_designer').offset().left - $('.texel_container').offset().left + $('.texel_designer').outerWidth() + 10) + 'px')

					$('.texel_container .texelToolbox').css({
						'top': ($('.texel_designer').offset().top - $('.texel_container').offset().top) + 'px',
						'left': ($('.texel_designer').offset().left - $('.texel_container').offset().left - 90) + 'px'
					});

					$('.texel_container .sides').css({
						'top': ($('.texel_designer').offset().top - $('.texel_container').offset().top) + 'px',
						'left': ($('.texel_designer').offset().left - $('.texel_container').offset().left + $('.texel_designer').outerWidth() + 10) + 'px'
					});
				}
			});
		}
		resizeTexel();

		$(window).off('resize.texelSize').on('resize.texelSize', function(e) {
			if (e.target.toString().match(/window/i) != null) {
				waitForFinalEvent(function () {
					resizeTexel();
				}, 250, 'texelSize');
			}
		});
	},

