	/**
	 * Create the navigator for the addressing tool to preview other addresses
	 * @param s7Obj
	 * @param el
	 * @param index
	 * @private
	 */
	_createAddressBrowser: function(s7Obj, el, index) {
		var self = this;
		if(typeof index === 'undefined' || index == null) {
			index = this.options.activeDesign;
		}

		var getS7ObjId = function(s7Objs) {
			var elementIDsToUpdate = [];
			$.each(s7Objs, function(idx) {
				elementIDsToUpdate.push(s7Objs[idx].id);
			});

			return elementIDsToUpdate;
		};

		el.append(
			$('<div/>').addClass('addressRotator').append(
				$('<div/>').addClass('addPrev').html('< Previous').on('click', function() {
					if(self._globalVars.designs[index].activeVariableData > 0) {
						var s7Objs = self._variableS7Obj(s7Obj);
						self._updateVariableData(s7Objs, self._globalVars.designs[index].variableData[--self._globalVars.designs[index].activeVariableData], null);
						self._paintTexel(self.options.activeDesign, false, getS7ObjId(s7Objs), false);
					}
				})
			).append(
				$('<div/>').addClass('addNext').html('Next >').on('click', function() {
					if(self._globalVars.designs[index].activeVariableData < self._globalVars.designs[index].variableData.length - 1) {
						var s7Objs = self._variableS7Obj(s7Obj);
						self._updateVariableData(s7Objs, self._globalVars.designs[index].variableData[++self._globalVars.designs[index].activeVariableData], null);
						self._paintTexel(self.options.activeDesign, false, getS7ObjId(s7Objs), false);
					}
				})
			)
		);
	},

	/**
	 * Public method used to create the VariableData Grid
	 * @param gridData - The grid data that need to be loaded with
	 * @param method - The entry method
	 * @param partyId - Partyid if available
	 * @param index
	 * @param paint - true if the side carrying the design should be painted and made active
	 * @returns {*|jQuery} - The grid instance
	 */
    createAddressingGrid: function(gridData, method, partyId, index, paint) {
		var self = this;

		if(typeof index === 'undefined' || index == null) {
			index = this.options.activeDesign;
		} else {
			if(paint) {
				self._setOption('activeDesign', index);
			} else {
				this.options.activeDesign = index;
			}
		}

		var gridOptions = $.extend({
			gridData      	: typeof gridData != 'undefined' ? gridData : [[]],
			sessionId     	: self._globalVars.sessionId,
			partyId       	: typeof partyId === 'undefined' || partyId == null ? '' : partyId,
			dataGroupId		: (self._globalVars.designs[index].addressBookId == null) ? -1 : parseInt(self._globalVars.designs[index].addressBookId),
			onGridApply   	: function(event, data) {
				self.options.activeDesign = index;
				self._globalVars.designs[index].addressBookId = data.dataGroupId + '';
				self._globalVars.designs[index].variableData = data.data;
				if (typeof productPage != 'undefined') {
					productPage.updateAddressingButton(data.data.length);
				}
				self.createCustomElement('RichText', index, (typeof self._globalVars.product.getProduct != 'undefined' ? self._globalVars.product.getProduct().variableSettings : self.getProduct().variableSettings));
			},
            onGridClose   : function(event, data) {
                self.closeTexelPopupContainers();
                self.element.addClass('hidden');
                $('body').removeClass('texelScrollRemoval');
            },
			dataGroupMode : method
		}, this.options.variableDataGridOptions || {});

		return $('#' + this.options.variableDataGridId).VariableDataGrid(gridOptions);
    },

