	/**
	 * Create a custom FXG DOM element - There can only be one variable data group on each index(side)
	 * @param {String} type - Either RichText or BitmapImage
	 * @param {String} index - The design index to create for
	 * @param {Object} variableSettings - Data for variable
	 */
	createCustomElement: function(type, index, variableSettings) {
		var self = this;
		var isVariable = (variableSettings != null);

		//check if any other variable data exists on this index, if so, exit
		if(isVariable) {
			var variableDataS7Obj = null;
			$.each(self._globalVars.designs[index].elements, function(idx) {
				if(!self._globalVars.designs[index].elements[idx].deleted && self._globalVars.designs[index].elements[idx].isVariable) {
					variableDataS7Obj = self._globalVars.designs[index].elements[idx];
					return false;
				}
			});
			if(variableDataS7Obj != null) {
				var s7Objs = self._variableS7Obj(variableDataS7Obj);
				self._updateVariableData(s7Objs, self._globalVars.designs[index].variableData[self._globalVars.designs[index].activeVariableData], null);
				self._paintTexel(index, true, null, true);
				return;
			}
		}

		self.closeTexelPopupContainers();

		if(typeof index === 'undefined' || index == null) {
			index = this.options.activeDesign;
		}

		var elId = type + '_' + ((isVariable) ? 'addressing' : (this._globalVars.designs[index].customCount++));

		function render(elId) {
			self._globalVars.designs[index].s7ElementQueue[elId] = new AjaxQueue(true, 3, true);
			self._paintTexel(index, true, [elId], false);
		}

		switch(type) {
			case 'RichText':
				var attrs = [{
					width: 200,
					height: 14,
					x: (this._globalVars.templateWidth / 2.0) - 100.0,
					y: (this._globalVars.templateHeight / 2.0) - 50.0,
					fontSize: 12,
					lineHeight: 14,
					textAlign: 'left',
					trackingRight: 2,
					fontFamily: 'Minion Pro',
					text: 'Enter your text here'
				}];

				function richTextXML(attr) {
					return $.parseXML(
						'<Graphic xmlns="http://ns.adobe.com/fxg/2008" xmlns:s7="http://ns.adobe.com/S7FXG/2008">'
						+ '<RichText fontFamily="' + attr.fontFamily + '" fontSize="' + attr.fontSize + '" lineHeight="' + attr.lineHeight + '" s7:colorName="Black" s7:colorValue="#ff" s7:colorspace="defined" s7:elementID="' + elId + '" textAlign="' + attr.textAlign + '" trackingRight="' + attr.trackingRight + '%" width="' + attr.width + '" height="' + attr.height + '" x="' + attr.x + '" y="' + attr.y + '">'
						+ '<content><p><span>' + charEncodingForScene7(attr.text) + '</span></p></content>'
						+ '</RichText>'
						+ '</Graphic>');
				}

				/*
				 If its a variable data object, we need to create the sister elements
				 All elements will be assigned a data-group-id which will be used to match settings between dom elements
				 */
				if(isVariable) {
					attrs = function createVariableData() {
						var defaultAttrs = [{
							width: self._globalVars.templateWidth - 10,
							x: (self._globalVars.templateWidth - (self._globalVars.templateWidth - 10)) / 2,
							fontSize: 30,
							lineHeight: 32,
							textAlign: 'center',
							trackingRight: 2,
							fontFamily: 'Bauderie Script SSi',
							text: 'Enter your text here'
						}/*,{
							width: self._globalVars.templateWidth - 10,
							x: (self._globalVars.templateWidth - (self._globalVars.templateWidth - 10)) / 2,
							fontSize: 12,
							lineHeight: 14,
							textAlign: 'center',
							trackingRight: 2,
							fontFamily: 'Open Sans',
							text: 'Enter your text here'
						}*/];

						$.extend(defaultAttrs[0], variableSettings[0], true);
						//$.extend(defaultAttrs[1], variableSettings[1], true);

						return self._updateVariableData(null, self._globalVars.designs[index].variableData[0], defaultAttrs);
					}();

					var s7Obj = this._createRichText($(richTextXML(attrs[0])).find('RichText'), true);
					s7Obj.groupId = elId;
					s7Obj.isVariable = true;
					this._globalVars.designs[index].elements.push(s7Obj);
					render(elId);

					/*
					elId = elId + '_' + 1;
					var s7Obj2 = this._createRichText($(richTextXML(attrs[1])).find('RichText'), true);
					s7Obj2.groupId = s7Obj.groupId;
					s7Obj2.isVariable = true;
					this._globalVars.designs[index].elements.push(s7Obj2);
					render(elId);
					*/
				} else {
					this._globalVars.designs[index].elements.push(this._createRichText($(richTextXML(attrs[0])).find('RichText'), true));
					render(elId);
				}

				break;
			case 'BitmapImage':
				function bitmapImageXML(attr) {
					return $.parseXML(
						'<Graphic xmlns="http://ns.adobe.com/fxg/2008" xmlns:s7="http://ns.adobe.com/S7FXG/2008">'
						+ '<Group s7:referencePoint="inherit" x="' + 0 + '"' + ' y="' + 0 + '">'
						+ '<BitmapImage width="400" height="200" s7:elementID="' + elId + '" s7:fillOverprint="false" s7:fillOverprintMode="true" s7:fit="stretch" s7:referencePoint="center" s7:strokeOverprint="false" s7:strokeOverprintMode="true" scaleX=".5" scaleY=".5" source="@Embed(\'8001A.assets/images/image_library_icon.jpg\')"/>'
						+ '</Group>'
						+ '</Graphic>');
				}

				var defaultAttrs = {
					x: 0,
					y: 0
				};

				this._globalVars.designs[index].elements.push(this._createBitmapImage($(bitmapImageXML(defaultAttrs)).find('BitmapImage'), true));
				render(elId);

				break;
		}

		$('#' + elId).trigger('click');
	},

