	/**
	 * Get Font List for displaying.
	 * @param el
	 * @returns {string}
	 */
	getFontList: function(el) {
		var s7Obj = el.data('Scene7Element');
		var fontList = '';

		if(this._fonts.indexOf(s7Obj.fontFamily) == -1) {
			fontList += '<div class="texelListItem texelSelectListSelected" data-value="' + s7Obj.fontFamily + '"><img src="/html/img/fonts/' + s7Obj.fontFamily.toLowerCase().replace(/\s/g,'') + '.png" /></div>';
		}

		$.each(this._fonts, function(i, v) {
			fontList += '<div class="texelListItem' + ((v === s7Obj.fontFamily) ? ' texelSelectListSelected' : '') + '" data-value="' + v + '"><img src="/html/img/fonts/' + v.toLowerCase().replace(/\s/g,'') + '.png" /></div>';
		});

		return fontList;
	},

	/**
	 * Get Color List for displaying.
	 * @param el
	 * @returns {string}
	 */
	getColorList: function(el) {
		var self = this;
		var s7Obj = el.data('Scene7Element');
		var colorList = '';
		var selectedColor = '';

		$.each(this._colors, function(i, v) {
			if (self._globalVars.product != null && typeof self._globalVars.product.getProduct != 'undefined' && !self._globalVars.product.getProduct().hasWhiteInk && v.hex == '#ffffff') {
				return true;
			}

			if (self._globalVars.product != null && typeof self._globalVars.product.getProduct != 'undefined' && typeof self._globalVars.product.getProduct().color !== 'undefined' && typeof self._globalVars.allowedColors[self._globalVars.product.getProduct().color] !== 'undefined') {
				if (typeof self._globalVars.allowedColors[self._globalVars.product.getProduct().color][v.name] !== 'undefined' && self._globalVars.allowedColors[self._globalVars.product.getProduct().color][v.name]) {
					if(v.hex === s7Obj.color.toLowerCase()) {
						selectedColor = s7Obj.color;
					}
					colorList += '<div style="background-color: ' + v.hex + ';" class="texelListItem' + ((v.hex === s7Obj.color.toLowerCase()) ? ' texelSelectListSelected"' : '') + '" data-value="' + v.hex + '">' + v.name + '</div>';
				}
			}
			else {
				if (self._isColorAllowed(self.options.backgroundColor, v.hex) || v.hex == "#ffffff") {
					if(v.hex === s7Obj.color.toLowerCase()) {
						selectedColor = s7Obj.color;
					}
					colorList += '<div style="background-color: ' + v.hex + ';" class="texelListItem' + ((v.hex === s7Obj.color.toLowerCase()) ? ' texelSelectListSelected"' : '') + '" data-value="' + v.hex + '">' + v.name + '</div>';
				}
			}
		});

		if(selectedColor == '') {
			colorList += '<div style="background-color: ' + s7Obj.color + ';" class="texelListItem texelSelectListSelected" data-value="' + s7Obj.color + '">' + s7Obj.color + '</div>';
		}

		return colorList;
	},

