	/**
	 * Get the data for a uploaded image to scene7 ugc
	 * @param el
	 * @param s7Obj
	 * @param loadImage - If true, then auto apply the image that was uploaded
	 * @private
	 */
	_paintUserImages: function(el, s7Obj, loadImage) {
		var self = this;

		$('.texelPopupContainer .uploadGrid').empty();
		$.each(self._globalVars.userImages, function(idx) {
			$('.texelPopupContainer .uploadGrid').append($('<img/>').attr({ 'data-url': self._globalVars.userImages[idx], 'src': self._globalVars.userImages[idx] + '?fmt=png-alpha&wid=75&hei=75'}).on('click', function() {
				if($(this).attr('data-url').indexOf('fxg') != -1) {
					var fxgId = $(this).attr('data-url').match(/.*?\/(\d+)\.fxg/);
					/*
					 If we want to insert the elements from a ugc fxg as custom elements in the current design, do below
					 this code still needs work, we have to remove the element that we are pushing the image to, and then
					 add all the elements from the fxg using insertAfter
					 */
					//self._processFXGXML(fxgId[1]);

					//otherwise pass it and embed it
					var ugcFXGXML = getFXGXML(fxgId[1], true);
					var ugcFXGWidth = parseFloat($(ugcFXGXML).find('Graphic').attr('viewWidth'));
					var ugcFXGHeight = parseFloat($(ugcFXGXML).find('Graphic').attr('viewHeight'));
					self._updateImageAttr(s7Obj, { 'width': ugcFXGWidth, 'height': ugcFXGHeight}, 'http:' + $(this).attr('data-url'));
				} else {
					self._updateImageAttr(s7Obj, self._getImageAttr('http:' + $(this).attr('data-url')), 'http:' + $(this).attr('data-url'));
				}
			}));
		});

		if(loadImage) {
			$($('.texelPopupContainer .uploadGrid img')[0]).trigger('click');
		}

		//if no images have been found auto click the upload option to trigger file selection
		if(self._globalVars.userImages.length == 0) {
			$('.texelPopupContainer').find('#ugcFileBut').trigger('click');
		}
	},

	/**
	 * Get the image dimensions from scene7 for the ugc file
	 * @param url
	 * @private
	 */
	_getImageAttr: function(url) {
		var dataAttr = {};
		$.ajax({
			type: 'GET',
			timeout: 5000,
			url: 'https://texel.envelopes.com/getImageAttr?url=' + url,
			dataType: 'json',
			async : false
		}).done(function (data) {
			dataAttr = data;
		});

		return dataAttr;
	},

	/**
	 *
	 * @param s7Obj
	 * @param data
	 * @param url
	 * @private
	 */
	_updateImageAttr: function(s7Obj, data, url) {
		var scale = 1;

		if(parseFloat(data.width) > (this._globalVars.templateWidth) && ((this._globalVars.templateWidth) / parseFloat(data.width)) <= ((this._globalVars.templateHeight) / parseFloat(data.height))) {
			scale = (this._globalVars.templateWidth / 3) / parseFloat(data.width);
		} else if(parseFloat(data.height) > (this._globalVars.templateHeight) && ((this._globalVars.templateHeight) / parseFloat(data.height)) <= ((this._globalVars.templateWidth) / parseFloat(data.width))) {
			scale = (this._globalVars.templateHeight / 3) / parseFloat(data.height);
		}

		s7Obj.scaleX = scale;
		s7Obj.scaleY = scale;
		s7Obj.width = parseFloat(data.width);
		s7Obj.height = parseFloat(data.height);
		s7Obj.lastCropWidth = s7Obj.width;
		s7Obj.lastCropHeight = s7Obj.height;
		s7Obj.cropStartX = 0;
		s7Obj.cropStartY = 0;
		s7Obj.leftCropAmt = 0;
		s7Obj.topCropAmt = 0;
		s7Obj.rightCropAmt = 0;
		s7Obj.bottomCropAmt = 0;
		s7Obj.cropWidth = s7Obj.width;
		s7Obj.cropHeight = s7Obj.height;
		s7Obj.imageAttrWidth = s7Obj.width;
		s7Obj.imageAttrHeight = s7Obj.height;

		s7Obj.url = url.replace('http://actionenvelopew2p.scene7.com/is/image/','').replace('http://actionenvelopew2p.scene7.com/is/agm/',''); //cleanup for non-vector ugc
		s7Obj.url = (s7Obj.url.indexOf('fxg') != -1) ? s7Obj.url.replace('ActionEnvelope/', '') : s7Obj.url; //cleanup for vector ugc

		this._submitPopupChanges(s7Obj);
	},

